<script type="text/javascript">
    $(document).ready(function () {

        $(document.body).on('click', '.quantity-adder .add-action', function () {

            if ($(this).hasClass('add-up')) {
                $(this).parent().find('.prodquantity').val(parseInt($(this).parent().find('.prodquantity').val()) + 1);

                if (!$(this).parent().find(".add-down").hasClass('add-action')) {
                    $(this).parent().find(".add-down").addClass("add-action");
                }

            } else {
                if (parseInt($(this).parent().find('.prodquantity').val()) > 0) {
                    $(this).parent().find('.prodquantity').val(parseInt($(this).parent().find('.prodquantity').val()) - 1);
                }
                if (parseInt($(this).parent().find('.prodquantity').val()) == 0) {
                    $(this).removeClass("add-action");
                }

            }

            var pid = $(this).parents(".quantity-adder").attr("pid");
            var ekid = $(this).parents(".quantity-adder").attr("ekid");
            var price = $(this).parents(".quantity-adder").attr("price");
            var title = $(this).parents(".quantity-adder").attr("title");
            var s = $(this).parents(".quantity-adder").attr("s");
            var total = $("#prod-" + pid).attr("total");
            var addprototal = $("#prod-" + pid).attr("addprototal");

            if (s == "urun") {

                if (parseInt($(this).parent().find('.prodquantity').val()) > 0) {

                    if ($(this).hasClass('add-up')) {
                        var tt = parseFloat(total) + parseFloat(price);
                    } else {
                        var tt = parseFloat(total) - parseFloat(price);
                    }
                    var totalpricemain = $(this).parent().find('.prodquantity').val() * parseFloat(price);
                    $("#prod-" + pid).attr("total", tt);
                    $("#prod-" + pid).find(".birimfiyat").html(fixprice(totalpricemain));
                    $("#prod-" + pid).find(".toplamfiyat").html(fixprice(tt));


                } else {

                    var vr = $(this).parents(".quantity-adder").attr("gid");

                    bootbox.setDefaults({locale: "tr"});
                    bootbox.confirm("Ürün sepetinizden silinecek. Emin misiniz?", function (result) {
                        if (result == true) {
